import React, { Component } from 'react';

import './App.css';
import { Formik, Form, ErrorMessage, Field } from 'formik';
import * as yup from 'yup';

class App extends Component {
  constructor() {
    super();

    this.state = {
      test: 'hello',
      attempt: false
    };
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Formik
            initialValues={{ email: '', comment: '' }}
            validationSchema={() =>
              yup.object().shape({
                email: yup.string()
                  .email('not a valid email')
                  .required('must be entered'),
                comment: yup.string().min(4, 'must be 4 chars').required('must be entered')
              })
            }
            onSubmit={(values, { setSubmitting }) => {
              this.setState({
                attempt: true
              })
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
              }, 400);
            }}
          >
            {({ isSubmitting, dirty }) => (
              <Form>
                  <Field type="email" name="email" placeholder="email please" />
                  <this.state.attempt ErrorMessage name="email" component="div" />

                  <Field type="comment" name="comment" placeholder="comment please" />
                  <ErrorMessage name="comment" component="div" />

                  <button type="go" disabled={isSubmitting || !dirty}>
                    Submit and dirty is {dirty ? 'ues' : 'no'} {isSubmitting}
                  </button>
              </Form>
            )}
          </Formik>
        </header>
      </div>
    );
  }
}

export default App;
