import React, { Component } from 'react';
import './App.css';
import {data} from './searchData';
import SearchItem from './searchItem';

class SearchComponent extends Component {
    constructor() {
        super();

        this.state = {
            searchValues: '',
            data: {...data},
        }
        console.log(this.state)
    };

    handleSearch = () => {
        console.log('handle search', this.state.searchValues)
    };

    handleChange = (val) => {
        console.log('new value is', val.target.value)
        this.setState({
            searchValues: val.target.value
        })
    }

    itemsList = () => {
        var test = this.state.data.groups.reduce((groups, currentValue, currentIndex) => {
            currentValue.items.map(item => {
                if (item.displayName.includes(this.state.searchValues)) {
                    groups[currentIndex] = {
                        items: []
                    };
                    groups[currentIndex].displayName = currentValue.displayName;
                    groups[currentIndex].items.push(item) 
                } else {
                    return groups
                }
            })
            return groups
        }, [])
        return test;
    }

    render () {
        let dataToDisplay = 
        this.state.searchValues=='' || this.state.searchValues==null ?
        this.state.data : this.state.data
        return (
            <React.Fragment>
                <input type="text" name="search" placeholder="search term" value={this.state.searchValues} onChange={this.handleChange} />
                <button type="button" name="goSearch" onClick={this.handleSearch}>></button>

                {
                    dataToDisplay.groups.map(group => {
                        return (
                            <React.Fragment>
                                <hr />
                                <h1>Group is: {group.displayName}</h1>
                                {
                                    group.items.map(item => 
                                        <SearchItem props={{group, item}} />)
                                }
                            </React.Fragment>
                        )    
                    })
                }
            </React.Fragment>
        )
    }
};

export default SearchComponent;
