import React, { Component } from 'react';

import './App.css';
import { Formik, Form, ErrorMessage, Field, FieldArray } from 'formik';
import * as yup from 'yup';

class App extends Component {
  constructor() {
    super();

    this.state = {
      test: 'hello',
      attempt: false
    };
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <Formik
            initialValues={
              {
                email: '',
                comment: '',
                list: [
                  {name: 'one', checked: false,},
                  {name: 'two', checked: false},
                  {name: 'three', checked: false}
                ]
              }
            }
            validationSchema={() =>
              yup.object().shape({
                email: yup.string()
                  .email('not a valid email')
                  .required('must be entered'),
                comment: yup.string().min(4, 'must be 4 chars').required('must be entered')
              })
            }
            onSubmit={(values, { setSubmitting }) => {
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
              }, 400);
            }}
          >
            {({ isSubmitting, dirty, submitCount }) => (
              <Form>
                <Field type="email" name="email" placeholder="email please" />
                {submitCount > 0 &&
                  <ErrorMessage name="email" component="div" />
                }
                <Field type="comment" name="comment" placeholder="comment please" />
                {submitCount > 0 &&
                  <ErrorMessage name="comment" component="div" />
                }

                <FieldArray type="checkbox" name="chck" />
                <input type='type' name={name} checked={checked} onChange={onChange} />
                <button type="go" disabled={isSubmitting || !dirty}>
                  Submit and dirty is {dirty ? 'ues' : 'no'} {isSubmitting}
                </button>
              </Form>
            )}
          </Formik>
        </header>
      </div>
    );
  }
}

export default App;
