import React, { Component } from 'react';

import './App.css';
import { Formik, Form, ErrorMessage, Field, FieldArray, arrayHelpers } from 'formik';
import * as yup from 'yup';

class App extends Component {
  constructor() {
    super();

    this.state = {
      test: 'hello',
      attempt: false
    };
  }


  render() {
    const test = () => console.log('test')
    return (
      <div className="App">
        <header className="App-header">
          <Formik
            initialValues={
              {
                email: '',
                comment: '',
                list: [
                  {name: 'one', checked: false,},
                  {name: 'two', checked: false},
                  {name: 'three', checked: false}
                ]
              }
            }
            validate= {(values => {
              let errors = {};
              console.log(values)
              errors.list = values.list.some(element =>
                element.checked
            ) ? null : 'must select at least one from list'
                return errors;
          })}
            validateOnBlur={false}
            validateOnChange={false}
            validationSchema={
              yup.object().shape({
                email: yup.string()
                  .email('not a valid email')
                  .required('must be entered'),
                comment: yup.string().min(4, 'must be 4 chars').required('must be entered')
              })
            }
            onSubmit={(values, { setSubmitting }) => {
              setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
              }, 400);
            }}
            handleChange={() => console.log('handle change')}
          >
            {({ isSubmitting, dirty, values, handleChange }) => (
              <Form>
                <Field type="email" name="email" placeholder="email please" />
                <ErrorMessage name="email" component="div" />

                <Field type="comment" name="comment" placeholder="comment please" />
                <ErrorMessage name="comment" component="div" />

                <FieldArray name="list"
                render={arrayHelpers => (
                  <React.Fragment>
                    {values.list.map((value, index) => {
                      return (
                        <div key={value.name}> {value.name}
                        <input 
                        key={value.name} 
                        type='checkbox' 
                        name={value.name} 
                        checked={value.checked}
                        onChange={(e) => {
                          console.log(e.target);
                          arrayHelpers.replace(index, {...value, checked: e.value.checked} );}}
                         />
                        </div>
                      )
                    })}
                  </React.Fragment>

                )} />
                <ErrorMessage name="list" component="div" />
                <button type="go" disabled={isSubmitting || !dirty}>
                  Submit and dirty is {dirty ? 'ues' : 'no'} {isSubmitting}
                </button>
              </Form>
            )}
          </Formik>
        </header>
      </div>
    );
  }
}

export default App;
